import React from "react";
import { Link, Outlet, useNavigate } from "react-router-dom";
import "./Layout.css";

function Layout({ usuarioLogeado, setUsuarioLogeado }) {
  const navigate = useNavigate();

  const cerrarSesion = () => {
    localStorage.removeItem("usuario");
    setUsuarioLogeado(null);
    navigate("/login");
  };

  return (
    <div className="layout">
      {/* --- Encabezado --- */}
      <header
        className="header"
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          padding: "10px",
          backgroundColor: "#f5f5f5",
        }}
      >
        <h1>HelpDesk</h1>
        <div>
          <h3 style={{ display: "inline", marginRight: "15px" }}>
            Bienvenido {usuarioLogeado?.nombre || usuarioLogeado?.usuario}
          </h3>
          <button onClick={cerrarSesion}>Cerrar sesión</button>
        </div>
      </header>

      {/* --- Barra de navegacion --- */}
      <aside className="sidebar">
        <nav>
          <ul>
            <li>
              <Link to="/inicio">Inicio</Link>
            </li>
            <li>
              <Link to="/usuarios">Usuarios</Link>
              <Link to="/usuarios-inactivos">Usuarios Inactivos</Link>
            </li>
            <li>
              <Link to="/tickets">Tickets</Link>
              <Link to="/tickets-sin-asignar">Tickets Sin Asignar</Link>
            </li>
            <li>
              <Link to="/roles">Roles</Link>
            </li>
            <li>
              <Link to="/areas">Áreas</Link>
            </li>
          </ul>
        </nav>
      </aside>

      <main className="main-content">
        <Outlet />
      </main>
    </div>
  );
}

export default Layout;
