import React, { useState, useEffect } from "react";

function UsuariosInactivos() {
  const [usuarios, setUsuarios] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [error, setError] = useState("");

  const [roles, setRoles] = useState([]);
  const [areas, setAreas] = useState([]);

  const [mostrandoModal, setMostrandoModal] = useState(false);
  const [nuevoUsuario, setNuevoUsuario] = useState({nombre: "", apellido: "", correo: "", telefono: "", usuario: "", password: "", id_rol: "", id_area: "", });
  const [mensaje, setMensaje] = useState("");
  const [guardando, setGuardando] = useState(false);
  
  useEffect(() => {
    const obtenerUsuarios = async () => {
      try {
        const res = await fetch("http://localhost:3001/api/usuariosInactivos");
        if (!res.ok) throw new Error("Error al obtener usuarios");
        const datos = await res.json();
        setUsuarios(datos);
      } catch (err) {
        setError(err.message);
      } finally {
        setCargando(false);
      }
    };
    obtenerUsuarios();
  }, []);

  if (cargando) return <p>Cargando usuarios...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div>
      <h2>Lista de Usuarios Inactivos</h2>
      <table border="1" cellPadding="10" style={{ borderCollapse: "collapse", width: "100%" }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Correo</th>
            <th>Teléfono</th>
            <th>Rol</th>
            <th>Área</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {usuarios.map((usuario) => (
            <tr key={usuario.id_usuario}>
              <td>{usuario.id_usuario}</td>
              <td>{usuario.nombre_usuario}</td>
              <td>{usuario.correo}</td>
              <td>{usuario.telefono}</td>
              <td>{usuario.nombre_rol}</td>
              <td>{usuario.nombre_area}</td>
              <td>
                <button>Activar</button>                
              </td>
            </tr>
          ))}
        </tbody>
      </table>     
    </div>
  );
}

export default UsuariosInactivos;
