import React, { useState, useEffect } from "react";

function Tickets() {
  const [tickets, setTickets] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [error, setError] = useState("");
  const [ticketSeleccionado, setTicketSeleccionado] = useState(null); // ðŸ”¹ Ticket que se muestra en el modal
  const [tecnico, setTecnico] = useState("");
  const [prioridad, setPrioridad] = useState("");
  const [mostrandoModal, setMostrandoModal] = useState(false);

  useEffect(() => {
    const obtenerTickets = async () => {
      try {
        const respuesta = await fetch("http://localhost:3001/api/ticketsSinAsignar");
        if (!respuesta.ok) throw new Error("Error al obtener tickets");
        const datos = await respuesta.json();
        setTickets(datos);
      } catch (err) {
        setError(err.message);
      } finally {
        setCargando(false);
      }
    };

    obtenerTickets();
  }, []);

  // ðŸ”¹ FunciÃ³n para abrir el modal
  const abrirModal = (ticket) => {
    setTicketSeleccionado(ticket);
    setTecnico("");
    setPrioridad("");
    setMostrandoModal(true);
  };

  // ðŸ”¹ FunciÃ³n para cerrar el modal
  const cerrarModal = () => {
    setMostrandoModal(false);
    setTicketSeleccionado(null);
  };

  // ðŸ”¹ FunciÃ³n para asignar ticket
  const asignarTicket = async () => {
    if (!tecnico || !prioridad) {
      alert("Por favor selecciona un tÃ©cnico y una prioridad.");
      return;
    }

    try {
      const respuesta = await fetch("http://localhost:3001/api/asignarTicket", {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          id_ticket: ticketSeleccionado.id_ticket,
          tecnico,
          prioridad,
        }),
      });

      if (!respuesta.ok) throw new Error("Error al asignar ticket");

      alert("Ticket asignado correctamente âœ…");
      cerrarModal();

      // ðŸ”„ Recargar la lista
      const nuevosTickets = tickets.filter(t => t.id_ticket !== ticketSeleccionado.id_ticket);
      setTickets(nuevosTickets);
    } catch (err) {
      alert("Error: " + err.message);
    }
  };

  if (cargando) return <p>Cargando tickets...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div>
      <h2>Lista de Tickets Sin Asignar</h2>
      <table border="1" cellPadding="10" style={{ borderCollapse: "collapse", width: "100%" }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Usuario</th>
            <th>TÃ­tulo</th>
            <th>Fecha de CreaciÃ³n</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {tickets.map((ticket) => (
            <tr key={ticket.id_ticket}>
              <td>{ticket.id_ticket}</td>
              <td>{ticket.nombre_usuario}</td>
              <td>{ticket.titulo}</td>
              <td>{ticket.fecha_creacion}</td>
              <td>
                <button onClick={() => abrirModal(ticket)}>Detalles</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* ðŸ”¹ Modal simple */}
      {mostrandoModal && ticketSeleccionado && (
        <div
          style={{
            position: "fixed",
            top: 0, left: 0, right: 0, bottom: 0,
            backgroundColor: "rgba(0,0,0,0.6)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <div style={{
            background: "#fff",
            padding: "20px",
            borderRadius: "10px",
            width: "400px",
          }}>
            <h3>Detalles del Ticket</h3>
            <p><strong>ID:</strong> {ticketSeleccionado.id_ticket}</p>
            <p><strong>Usuario:</strong> {ticketSeleccionado.nombre_usuario}</p>
            <p><strong>TÃ­tulo:</strong> {ticketSeleccionado.titulo}</p>
            <p><strong>DescripciÃ³n:</strong> {ticketSeleccionado.descripcion}</p>
            <p><strong>Fecha:</strong> {ticketSeleccionado.fecha_creacion}</p>

            <div>
              <label>Prioridad: </label>
              <select value={prioridad} onChange={(e) => setPrioridad(e.target.value)}>
                <option value="">Selecciona</option>
                <option value="Alta">Alta</option>
                <option value="Media">Media</option>
                <option value="Baja">Baja</option>
              </select>
            </div>

            <div>
              <label>TÃ©cnico: </label>
              <input
                type="text"
                placeholder="Nombre del tÃ©cnico"
                value={tecnico}
                onChange={(e) => setTecnico(e.target.value)}
              />
            </div>

            <div style={{ marginTop: "15px" }}>
              <button onClick={asignarTicket}>Asignar</button>
              <button onClick={cerrarModal} style={{ marginLeft: "10px" }}>Cerrar</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Tickets;
