import React, { useState, useEffect } from "react";

function Roles() {
  const [roles, setRoles] = useState([]);
  const [cargando, setCargando] = useState(true);
  const [error, setError] = useState("");

  const [usuarios, setUsuarios] = useState([]);
  const [rolSeleccionado, setRolSeleccionado] = useState(null);
  const [cargandoUsuarios, setCargandoUsuarios] = useState(false);
  const [mostrandoModal, setMostrandoModal] = useState(false);

  // --- Cargar roles ---
  useEffect(() => {
    const obtenerRoles = async () => {
      try {
        const respuesta = await fetch("http://localhost:3001/api/roles");
        if (!respuesta.ok) throw new Error("Error al obtener roles");
        const datos = await respuesta.json();
        setRoles(datos);
      } catch (err) {
        setError(err.message);
      } finally {
        setCargando(false);
      }
    };
    obtenerRoles();
  }, []);

  // --- Abrir modal y cargar usuarios ---
  const verUsuarios = async (rol) => {
    setRolSeleccionado(rol);
    setMostrandoModal(true);
    setCargandoUsuarios(true);
    try {
      const res = await fetch(`http://localhost:3001/api/rolesUsuarios?id_rol=${rol.id_rol}`);
      if (!res.ok) throw new Error("Error al obtener usuarios");
      const datos = await res.json();
      setUsuarios(datos);
    } catch (err) {
      console.error(err);
      setUsuarios([]);
    } finally {
      setCargandoUsuarios(false);
    }
  };

  const cerrarModal = () => {
    setMostrandoModal(false);
    setRolSeleccionado(null);
    setUsuarios([]);
  };

  if (cargando) return <p>Cargando roles...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div>
      <h2>Lista de Roles</h2>
      <table border="1" cellPadding="10" style={{ borderCollapse: "collapse", width: "100%" }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Descripción</th>
            <th>Acciones</th>
            <th>
              <button>+</button> {/* Botón para agregar roles */}
            </th>
          </tr>
        </thead>
        <tbody>
          {roles.map((rol) => (
            <tr key={rol.id_rol}>
              <td>{rol.id_rol}</td>
              <td>{rol.nombre_rol}</td>
              <td>{rol.descripcion}</td>
              <td>
                <button>Eliminar</button>
                <button onClick={() => verUsuarios(rol)}>Usuarios asignados</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* --- Modal de usuarios --- */}
      {mostrandoModal && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: "rgba(0,0,0,0.6)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 1000,
          }}
        >
          <div
            style={{
              background: "#fff",
              padding: "20px",
              borderRadius: "10px",
              width: "400px",
              maxHeight: "80vh",
              overflowY: "auto",
            }}
          >
            <h3>Usuarios con el rol: {rolSeleccionado?.nombre_rol}</h3>

            {cargandoUsuarios ? (
              <p>Cargando usuarios...</p>
            ) : usuarios.length > 0 ? (
              <ul>
                {usuarios.map((u) => (
                  <li key={u.id_usuario}>{u.nombre_usuario || "(Sin nombre registrado)"}</li>
                ))}
              </ul>
            ) : (
              <p>No hay usuarios asignados a este rol.</p>
            )}

            <div style={{ marginTop: "15px", textAlign: "right" }}>
              <button onClick={cerrarModal}>Cerrar</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Roles;
