import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Login({ setUsuarioLogeado }) {
  const [usuario, setUsuario] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const usuarioGuardado = localStorage.getItem("usuario");
    if (usuarioGuardado) {
      // Si ya hay sesión activa, redirige directamente
      setUsuarioLogeado(JSON.parse(usuarioGuardado));
      navigate("/inicio");
    }
    setLoading(false);
  }, [navigate, setUsuarioLogeado]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const res = await fetch("http://localhost:3001/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ usuario, password }),
      });

      const data = await res.json();
      if (data.success) {
        localStorage.setItem("usuario", JSON.stringify(data.usuario));
        setUsuarioLogeado(data.usuario);

        // Redirige tras actualizar el estado
        setTimeout(() => navigate("/inicio"), 100);
      } else {
        setError("Usuario o contraseña incorrectos");
      }
    } catch (err) {
      console.error("Error al iniciar sesión:", err);
      setError("Ocurrió un error al intentar iniciar sesión");
    }
  };

  if (loading) return <p>Cargando...</p>;

  return (
    <div className="login-container">
      <h2>Iniciar Sesión</h2>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Usuario"
          value={usuario}
          onChange={(e) => setUsuario(e.target.value)}
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Entrar</button>
      </form>
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
}

export default Login;
